from telebot.types import InlineKeyboardButton,InlineKeyboardMarkupfrom shared import dbMigfrom utils import randwaiting= {}randnums=[]def manageLectures(bot,groupID):    markup=InlineKeyboardMarkup()    add=InlineKeyboardButton(('Add Lecture'),callback_data=f'addLecture_{groupID}')    get=InlineKeyboardButton(('Get Lecture'),callback_data=f'getLecture_{groupID}')    getAll=InlineKeyboardButton(('Get All Lectures'),callback_data=f'getAllLectures_{groupID}')    markup.add(add,get,getAll)    bot.send_message(groupID,'Select a one of buttons to do that',reply_markup=markup)def addLecture(bot,groupID):    _num=rand(randnums)    status= dbMig.addRequest(_num, groupID,"add_lecture")    if status!=True:        bot.send_message(groupID,'Error while adding request')    bot.send_message(groupID,'Yeah! Now you can add the lecture from this url:'+'\n'+f'http://127.0.0.1:8000/add-lecture/{_num}')def getAllLectures(bot,groupID):    list=dbMig.getAllLecture(groupID)    if list==None:        bot.send_message("Your request for getting all lectures, failed")    wholeMessage=""    markup=InlineKeyboardMarkup()    for item in list:        message=f"""        lecture name: {item[1]}        phone: {item[2]}        rate: {item[3]}        -----------------        """        wholeMessage=wholeMessage+message        btn=InlineKeyboardButton(f'{item[1]}',callback_data=f"lecture_{item[0]}_{groupID}")        markup.add(btn)    bot.send_message(groupID,wholeMessage+f"\nnumber of lectures: {len(list)}",reply_markup=markup)def getLecture(bot,lecID,groupID):    lec=dbMig.getLecture(lecID,groupID)    if lec==None:        bot.send_message(groupID,"Your request for getting lecture, failed")    message=f"""    lecture name: {lec[1]}    phone: {lec[2]}    rate: {lec[3]}"""    markup=InlineKeyboardMarkup()    edit=InlineKeyboardButton('edit',callback_data=f"editLecture_{lec[0]}_{groupID}")    delete=InlineKeyboardButton('delete',callback_data=f"deleteLecture_{lec[0]}_{groupID}")    markup.add(edit,delete)    bot.send_message(groupID,message,reply_markup=markup)def editLecture(bot,lecID,groupID):    status = dbMig.addRequest(lecID, groupID, "edit_lecture")    if status!=True:        bot.send_message(groupID,'Error while adding request')    bot.send_message(groupID,'Yeah! Now you can edit the lecture from this url:'+'\n'+f'http://127.0.0.1:8000/edit-lecture/{lecID}')def deleteLecture(bot,lecID,groupID):    status=dbMig.deleteLecture(lecID,groupID)    if status:        status=dbMig.delRequest(lecID)        if status:            bot.send_message(groupID, "The lecture successfully deleted")